import React from 'react';
import Axios from 'axios';
import { Launch } from '../Launch';
import "./styles.scss";

export class Favorites extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            favorites: props.favorites,
            launches: []
        }
    }
    componentDidMount = () => {

    }
    UNSAFE_componentWillReceiveProps = ({ favorites }) => {
        console.log(favorites);
        const launchList = [];
        console.log(favorites.map((fav) => {
            console.log("received next props");
            console.log(this.state);
            const queryString = "flight_number=" + fav;
            console.log('https://api.spacexdata.com/v3/launches/past?' + queryString);
            return Axios.get('https://api.spacexdata.com/v3/launches/past?' + queryString)
                .then((response) => {
                    let launch = response.data[0];
                    return <Launch
                        links={launch.links}
                        title={launch.mission_name}
                        date={launch.launch_date_utc}
                        desc={launch.details}
                        payloads={launch.rocket.second_stage.payloads}
                        number={launch.flight_number}
                        favoriteCallBack={this.props.favoriteCallBack}
                    />;
                }).catch((response) => {
                    console.log("error " + response);
                });
        }));
        console.log(launchList);
        this.setState({
            launches: launchList
        });
    }

    componentWillUnmount = () => {
        console.log("unmounted");
    }

    render = () => {
        return (
            <div className="favorites">
                {this.state.favorites.length > 0 && <h3>Favorites:</h3>}
                {this.state.launches}
            </div>
        );
    }
}
