import React from 'react';
import Axios from 'axios';
import { Launch } from '../Launch';
import "./styles.scss";

export class Favorites extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            favorites: props.favorites,
            launches: []
        }
    }
    componentDidMount = () => {

    }
    UNSAFE_componentWillReceiveProps = ({ favorites }) => {
        console.log(favorites);
        if (favorites) {
            console.log("received next props");
            console.log(this.state);
            const queryString = (favorites.map((fav) => {
                return "flight_number=" + fav;
            })).join("&");
            console.log('https://api.spacexdata.com/v3/launches/past?' + queryString);
            Axios.get('https://api.spacexdata.com/v3/launches/past?' + queryString)
                .then((response) => {
                    this.setState({
                        launches: response.data.map((launch, index) => {
                            return <Launch
                                key={"Launch" + index}
                                links={launch.links}
                                title={launch.mission_name}
                                date={launch.launch_date_utc}
                                desc={launch.details}
                                payloads={launch.rocket.second_stage.payloads}
                                number={launch.flight_number}
                                favoriteCallBack={this.props.favoriteCallBack}
                            />;

                        })
                    });
                }).catch((response) => {
                    console.log("error " + response);
                });
        }
    }

    componentWillUnmount = () => {
        console.log("unmounted");
    }

    render = () => {
        return (
            <div className="launches">
                {this.state.favorites.length>0 && <h3>Favorites:</h3>}
                {this.state.launches}
            </div>
        );
    }
}
